                .params("account","账号")
                .params("appName","公司名称或者app名称")
                .execute(new StringCallback() {
                    @Override
                    public void onSuccess(Response response) {
                        KLog.e(response.body());
                          new Handler().post(() -> Toast.makeText(ApActivity.this, response.body(), Toast.LENGTH_SHORT).show());
                        });
                    }
                });
    }

